import { ActionType } from "state/action-types";
import { Middleware } from "state/middleware/middleware";

let timer: any;

export const bundlerMiddleware: Middleware = (store) => (next) => (action) => {
  next(action);

  clearTimeout(timer);
  timer = setTimeout(() => {
    console.log("timer expired");
  }, 750);
};
