import MonacoEditor from "@monaco-editor/react";

const CodeEditor = function () {
  return (
    <MonacoEditor
      theme="vs-dark"
      language="javascript"
      height="350px"
      options={{
        wordWrap: "on",
        minimap: { enabled: false },
        showUnused: false,
        folding: false,
        lineNumbersMinChars: 3,
        fontSize: 16,
        scrollBeyondLastLine: false,
      }}
    />
  );
};

export default CodeEditor;
