import { ActionType } from "state/action-types";
import { Middleware } from "state/middleware/middleware";

let timer: any;

export const bundlerMiddleware: Middleware =
  ({ getState }) =>
  (next) =>
  (action) => {
    next(action);

    if (action.type !== ActionType.UPDATE_CELL) {
      return;
    }

    const {
      cells: { data },
    } = getState();

    clearTimeout(timer);
    timer = setTimeout(() => {
      console.log("timer expired");
    }, 750);
  };
