import MonacoEditor from "@monaco-editor/react";
import React from "react";

interface Props {
  initialValue: string;
}

const CodeEditor: React.FC<Props> = function ({ initialValue }) {
  return (
    <MonacoEditor
      value="const a = 1;"
      theme="vs-dark"
      language="javascript"
      height="350px"
      options={{
        wordWrap: "on",
        minimap: { enabled: false },
        showUnused: false,
        folding: false,
        lineNumbersMinChars: 3,
        fontSize: 16,
        scrollBeyondLastLine: false,
        automaticLayout: true,
      }}
    />
  );
};

export default CodeEditor;
